import React, { useRef, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { Pagination, Navigation, Autoplay, EffectCoverflow } from "swiper";

function Home() {
  return (
    <>
      <div className="mt-6 flex items-center justify-between px-10">
        <h3 className="text-4xl">EXCLUSIVE GAMES</h3>
        <h3 className="text-4xl">
          SEE MORE
          <img
            className="ml-3 inline-block w-5 -rotate-90 cursor-pointer"
            src="/assets/icons/angle-down.svg"
            alt="see more"
          />
        </h3>
      </div>

      <Swiper
        effect="coverflow"
        grabCursor="true"
        centeredSlides="true"
        slidesPerView={
          window.innerWidth > 1000 ? 4 : window.innerWidth > 600 ? 3 : 2
        }
        spaceBetween={30}
        pagination={{
          clickable: true,
        }}
        navigation={true}
        loop={true}
        autoplay={{
          delay: 2500,
        }}
        coverflowEffect={{
          spaceBetween: 30,
          rotate: 0, // slide rotation degree
          stretch: 30, // stretches the space between the slides in px
          depth: 0, // offsets the depth of neighboring slides
          modifier: 1, // effect multiplier
          slideShadows: true, // disables the shadow around the slide container
        }}
        breakpoints={{
          700: {
            spaceBetween: 0,
            slidesPerView: 4,
          },
          500: {
            spaceBetween: 100,
            slidesPerView: 2,
          },
          411: {
            spaceBetween: 100,
            slidesPerView: 2,
          },
          300: {
            spaceBetween: 0,
            slidesPerView: 1,
          },
        }}
        modules={[Pagination, Navigation, Autoplay, EffectCoverflow]}
        className="mySwiper"
      >
        <SwiperSlide>
          <img
            className=" h-full w-full bg-black/50"
            src="https://cdn1.epicgames.com/offer/b7b42e2078524ab386a8b2a9856ef557/EGS_SIFUStandardEdition_Sloclap_S4_1200x1600-32aca69d756abfcc25f8581942a6162b_1200x1600-32aca69d756abfcc25f8581942a6162b?h=854&resize=1&w=640"
            alt="SIFU"
